 class student{ public: int rollno; private: int age; public: void display(){ cout<<"age"<<age<<"roll no is"<<rollno<<endl; } student(int a,int b){ age=a; rollno=b; } student(int c){ age=c; } }; #include <iostream> using namespace std; int main() { student s1; student s2; student* s3=new student; s1.student(10,20); s2.student(20,30); s3->student(10); s1.display(); s2.display(); s3.display(); return 0; }